package set2;

import java.util.HashMap;

class CountIndex {
    int count, index;

    // constructor for first occurrence
    public CountIndex(int index) {
        this.count = 1;
        this.index = index;
    }

    // method for updating countRecursive
    public void incCount() {
        this.count++;
    }
}

public class FirstNonRepeatingCharacterInString {
    static final int NO_OF_CHARS = 256;

    static HashMap<Character, CountIndex> hm = new HashMap<Character, CountIndex>(NO_OF_CHARS);

    /* calculate countRecursive of characters
       in the passed string */
    static void getCharCountArray(String str) {
        for (int i = 0; i < str.length(); i++) {
            // If character already occurred,
            if (hm.containsKey(str.charAt(i))) {
                // updating countRecursive
                hm.get(str.charAt(i)).incCount();
            }
            // If it's first occurrence, then store the index and countRecursive = 1
            else {
                hm.put(str.charAt(i), new CountIndex(i));
            }

        }
    }

    /* The method returns index of first non-repeating
       character in a string. If all characters are repeating
       then returns -1 */
    static int firstNonRepeating(String str) {
        getCharCountArray(str);
        int result = Integer.MAX_VALUE, i;

        for (i = 0; i < str.length(); i++) {
            // If this character occurs only once and appears
            // before the current result, then update the result
            if (hm.get(str.charAt(i)).count == 1 && result > hm.get(str.charAt(i)).index) {
                result = hm.get(str.charAt(i)).index;
            }

        }
        return result;
    }

    private static int firstNonRepeatingMethod2(String str) {
        int NO_OF_CHARS = 256;
        int arr[] = new int[NO_OF_CHARS];
        for (int i = 0; i < NO_OF_CHARS; i++)
            arr[i] = -1;
        for (int i = 0; i < str.length(); i++) {
            if (arr[str.charAt(i)] == -1)
                arr[str.charAt(i)] = i;
            else
                arr[str.charAt(i)] = -2;
        }
        int res = Integer.MAX_VALUE;
        for (int i = 0; i < NO_OF_CHARS; i++) {
            if (arr[i] > 0)
                res = Math.min(res, arr[i]);
        }
        return res;
    }

    // Driver method
    public static void main(String[] args) {
        String str = "";
        long startTime = System.currentTimeMillis();
        int index = firstNonRepeating(str);
        System.out.println("timetaken: " + (System.currentTimeMillis() - startTime));

        System.out.println(index == Integer.MAX_VALUE ? "Either all characters are repeating " +
                " or string is empty" : "First non-repeating character is " + str.charAt(index));

        startTime = System.currentTimeMillis();
        System.out.println("firstNonRepeatingMethod2:" + str.charAt(firstNonRepeatingMethod2(str)));
        System.out.println("timetaken: " + (System.currentTimeMillis() - startTime));

    }
}
// Java program to find first non-repeating character
// Note : hashmap is used


